func showPowerCheckDialog() {
    if Thread.current.isMainThread == false {
        RunLoop.current.run()
    }

    let intent = Intent(context: targetContext, activityClass: SyncedFoldersActivity.self)
    let activity = activityRule.launchActivity(intent: intent)

    let sut = activity.buildPowerCheckDialog()

    DispatchQueue.main.async {
        sut.show()
    }

    RunLoop.current.run(until: Date(timeIntervalSinceNow: 0.1))
    shortSleep()

    if let window = sut.window {
        screenshot(view: window.decorView)
    }
}
