override func run(client: OwnCloudClient) -> RemoteOperationResult {
    guard let share = getStorageManager().getShareById(shareId) else {
        return RemoteOperationResult(resultCode: .shareNotFound)
    }

    let updateOperation = UpdateShareRemoteOperation(remoteId: share.getRemoteId())
    updateOperation.setNote(note)
    var result = updateOperation.execute(client: client)

    if result.isSuccess() {
        let getShareOp = GetShareRemoteOperation(remoteId: share.getRemoteId())
        result = getShareOp.execute(client: client)
        if result.isSuccess() {
            if let updatedShare = result.getData().first as? OCShare {
                getStorageManager().saveShare(updatedShare)
            }
        }
    }

    return result
}
