override func run(client: OwnCloudClient) -> RemoteOperationResult {
    let publicShare = getStorageManager().getShareById(shareId)

    let updateOp = UpdateShareRemoteOperation(remoteId: publicShare.getRemoteId())
    updateOp.setPassword(password)
    updateOp.setExpirationDate(expirationDateInMillis)
    updateOp.setHideFileDownload(hideFileDownload)
    updateOp.setLabel(label)

    var result = updateOp.execute(client: client)

    if result.isSuccess() {
        let getShareOp = GetShareRemoteOperation(remoteId: publicShare.getRemoteId())
        result = getShareOp.execute(client: client)
        if result.isSuccess() {
            if let share = result.getData().first as? OCShare {
                getStorageManager().saveShare(share)
            }
        }
    }

    return result
}
